document.addEventListener("DOMContentLoaded",function(){fetch("products.json").then(e=>e.json()).then(e=>{const t=document.getElementById("products");e.forEach(e=>{var n=document.createElement("div");n.className="swiper-slide";let i="";e.discount&&(i=`<div class="discount"><p><span>${e.discount}</span>Off</p></div>`),n.innerHTML=`
                  <div>
                      ${i}
                      <img src="assets/${e.image}" alt="${e.name}">
                  </div>
                  <h3>${e.name}</h3>
                  <p class="price">${e.price}</p>
                  <p class="price-discount">${e.priceDescount}</p>
                  <p class="parcels"><span>${e.parcels}x</span> de <span>${e.valueParcels}</span> sem juros</p>
                  <button class="add-to-cart">Adicionar ao Carrinho</button>
              `,t.appendChild(n)});new Swiper(".swiper",{direction:"horizontal",slidesPerView:1,loop:!0,loopAdditionalSlides:1,pagination:{el:".swiper-pagination"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},breakpoints:{991:{slidesPerView:4}},on:{slideNextTransitionEnd:function(){this.isEnd&&this.slideTo(0)},init:function(){this.slides.length<=this.params.slidesPerView&&(this.params.loop=!1,this.loopDestroy())},slidePrevTransitionEnd:function(){this.isBeginning&&this.slideTo(this.slides.length-1)}}});document.querySelectorAll(".add-to-cart").forEach(e=>{e.addEventListener("click",function(){var e=this.parentElement.querySelector("h3").innerText;alert(e+" foi adicionado ao carrinho!")})})}).catch(e=>console.error("Erro ao carregar os produtos:",e));var e=document.querySelector(".hamburger");const n=document.querySelector(".nav-container");e.addEventListener("click",()=>{n.classList.toggle("show")})});